import fs from "fs";
import path from "path";

export function getPostIds(){
    const postsDirectory = path.join(process.cwd(), "content");
    const fileNames = fs.readdirSync(postsDirectory);
    return fileNames.map(f => {
        return {
            params: {
                id: f.replace(/\.md$/, "")
            }
        };
    });
}
